import { Router } from 'express'
const router=Router();
import * as  productoController from '../controllers/producto.controller';
router.get('/listar',productoController.readAllproducto); 
router.post('/agregar',productoController.agregarProducto); 
router.get('/:id',productoController.getproductoId); 
router.delete('/eliminar/:id' , productoController.deleteProducto);
router.post('/actualizar/:id' , productoController.editarProducto);
export default router;

