sourcesContent:
import { pool } from "../database"



export const readAllproducto=async(req,res)=>{
  
  pool.query('select * from productos;', function (err, result) {
    try {
        return res.status(200).json(result);
    } catch (error) {
        return res.status(500).json('Error al mostrar productos' + e);
    }
  });
};

export const agregarProducto = async (req,res)=>{
    const {nombre,precio,stock,idcategoria} = req.body;
    pool.query('insert into productos(nombre, precio, stock, estado, idcategoria) values (?,?,?,1,?)',[nombre,precio,stock,idcategoria], function(err, result) {
    try {
      return res.status(200).json(
        `El producto ${ nombre } se ha agregado correctamente...!` //alt 96
    );
    } catch (e) {
        return res.status(500).json('Error al insertar la Escuela');
    }
  });
  };

  


export const getproductoId = async (req,res)=>{
  const id = parseInt(req.params.id);
  pool.query('select * from productos where idproducto = ?;',[id], function(err, result) {
  try {
      return res.status(200).json(result);
  } catch (e) {
      return res.status(500).json('Error al mostrar el producto');
  }
});
};





export const deleteProducto = async (req,res)=>{
  const id = parseInt(req.params.id);
  pool.query('delete from productos where idproducto = ?;',[id], function(err, result) {
  try {
      return res.status(200).json(result);
  } catch (e) {
      return res.status(500).json('Error al eliminar la producto');
  }
});
};




export const editarProducto = async (req,res)=>{
  const id = parseInt(req.params.id);
  const {nombre,precio,stock,idproducto} = req.body;

  pool.query('update productos set nombre = (?), precio = (?), stock = (?) where idproducto = (?)',[nombre,precio,stock,idproducto], function(err, result) {
  try {
    return res.status(200).json(
      `El producto con id  ${ id } se ha editado correctamente...!` //alt 96
  );
  } catch (e) {
      return res.status(500).json('Error al insertar la Escuela');
  }
});
};

