sourcesContent:
import { pool } from "../database"



export const readAllproducto=async(req,res)=>{
  
  try {
    const id = parseInt(req.params.id);
    pool.query(
      "select * from productos",
      [id],
      function (err, result) {
        console.log(result);
        try {
          return res.status(200).json(result);
        } catch (error) {
          return res.status(500).json("Error al listar escuela");
        }
      }
    );
  } catch (error) {
    return res.status(500).json("Error al listar escuela");
  }
};

export const agregarProducto = async (req,res)=>{

  try {
    const nombre = req.body.nombre;
    const precio = parseInt(req.body.precio);
    const stock = parseInt(req.body.stock);
    const estado = parseInt(req.body.estado);
    const idcategoria = parseInt(req.body.idcategoria);
    pool.query(
      "INSERT INTO productos (nombre, precio, stock, estado, idcategoria) VALUES(?,?,?,?,?); ",
      [nombre, precio, stock, estado, idcategoria],
      function (err, result) {
        console.log(result);
        try {
          return res.status(200).json(result);
        } catch (error) {
          return res.status(500).json("Error al listar escuela");
          
        }

      }
    );
  } catch (error) {
    return res.status(500).json("Error al listar escuela");
  }
};

  


export const getproductoId = async (req,res)=>{
  const id = parseInt(req.params.id);
  pool.query('select * from productos where idproducto = ?;',[id], function(err, result) {
  try {
      return res.status(200).json(result);
  } catch (e) {
      return res.status(500).json('Error al mostrar el producto');
  }
});
};





export const deleteProducto = async (req,res)=>{
  try {
    const id = parseInt(req.params.id);

    pool.query(
      "DELETE FROM productos WHERE idproducto=?; ",
      [id],
      function (err, result) {
        try {
          return res.status(200).json(result);
        } catch (error) {
          return res.status(500).json("Error al listar escuela");
        }
      }
    );
  } catch (error) {
    return res.status(500).json("Error al listar escuela");
  }
};




export const editarProducto = async (req,res)=>{
  try {
    const id = parseInt(req.params.id);
    const nombre = (req.body.nombre);
    const precio = parseInt(req.body.precio);
    const stock = parseInt(req.body.stock);
    const estado = parseInt(req.body.estado);
    const idcategoria = parseInt(req.body.idcategoria);
    
    pool.query(
      "update productos SET nombre=?, precio=? , stock=? , estado=? , idcategoria=? WHERE idproducto=?; ",
      [nombre, precio ,stock , estado, idcategoria, id],
      function (err, result) {
        try {
          return res.status(200).json(result);
        } catch (error) {
          return res.status(500).json("Error al listar escuela");
        }
      }
    );
  } catch (error) {
    return res.status(500).json("Error al listar escuela");
  }
};

